INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (2, 2, "Bishops Bluecoat CoE High School", "bishops-bluecoat", "We are passionate about education and know that students, staff and parents working together create an outstanding climate for learning. There is a tangible sense of pride and positivity within our school, stemming from the belief that each student will be known, nurtured and inspired to progress academically and grow as a person.  There is very effective provision for learning and personal development; our values prepare students exceptionally well to take their place in modern Britain. Known, nurtured and inspired: the young people of Bishops' will succeed and make a positive difference to the world in which we live.", "http://www.bishopschester.co.uk", "bishops-bluecoat-logo.jpg", "bishops-bluecoat");
