INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 2, "Delamere CofE Primary Academy", "delamere-ce-primary", "Delamere CofE Primary Academy is an 'Outstanding School', situated near Tarporley in Cheshire. At Delamere we are proud to deliver a rich creative learning experience that enables all the children to fulfil their potential. The Academy goes 'above and beyond' with art, we believe that through the arts we enrich the lives of all children. Arts subjects stimulate creativity and imagination and enable children to communicate what they see, feel and think about the real and imaginary world in a variety of ways and through different mediums.", "http://www.delamereacademy.co.uk", "delamere-logo.jpg", "delamere-ce-primary");
