INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (2, 2, "Great Sankey High School", "great-sankey-high", "Great Sankey High School in Warrington is a place where every child is recognised as an individual, where their talents are nurtured, aspirations exceeded and where we collectively strive to achieve more.  Academic attainment and achievement is high – our personalised curriculum empowers all students to achieve their ambitions and fulfil their high aspirations. Oou vision in the art department at Great Sankey High School is to create an environment in which every child can feel confident and succeed. To encourage individual creativity and nurture a passion for the subject. We are very proud of the work our students create!", "http://www.greatsankey.org", "great-sankey-high-logo.jpg", "great-sankey-high");
