INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 2, "Green Lane Community Special Primary School", "green-lane-special-primary", "Green Lane is a school which caters for 139 pupils, ranging from age 4 to 18. Pupils have a wide range of special needs, from moderate to severe and around half have communication difficulties, including ASD. Staff at school are extremely committed to the personal and social development of the pupils and strive to deliver a rich and balanced curriculum promoting independence, resilience and creativity.  This is no more apparent than in the teaching of art.  Every pupil is encouraged to develop their senses through their art work and express their ideas, feelings and imagination using visual media.", "http://www.greenlaneschool.co.uk", "green-lane-special-primary-logo.jpg", "green-lane-special-primary");
