INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 2, "Mottram St Andrew Primary Academy", "mottram-st-andrew-primary", "We are a small school of 160 pupils in Cheshire. We are part of the Fallibroome Learning Community and support a creative curriculum where art is a significant part. Year 5 have produced 3-d pop art cityscapes looking at the work of Charles Fazzino. Submission 1 is a canvas produced in art club", "http://www.thelifecloud.net/schools/MottramStAndrewPrimaryAcademy", "mottram-st-andrew-logo.jpg", "mottram-st-andrew-primary");
