INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 2, "Sacred Heart Catholic Primary", "sacred-heart-catholic-primary", "At Sacred Heart Catholic Primary School in Warrington we aim to provide a warm, happy and stimulating environment in which children enjoy learning and we are able to appreciate its importance and relevance to their lives. We are all for the education of the 'whole child', and truly believe the development of Art skills will enable the achievement of personal fulfillment and satisfaction of the child. We feel that success increases self esteem and gives pleasure in the subject. We aim to display work when possible to celebrate pupil achievement and to enable pupil satisfaction both inside and outside of the classroom environment. We have art ambassadors who choose children to receive an award every week to celebrate achievement in a creative non academic sense.", "https://www.sacredheartcp.co.uk", "sacred-heart-logo.jpg", "sacred-heart-primary");
