INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (2, 2, "St Nicholas Catholic High School", "st-nicholas-catholic-high", "We are an 11-18 co-educational Catholic Comprehensive School in Hartford with Business and Enterprise College status and a second specialism as a Leading Edge school.The school has an excellent reputation and provides for its students an environment that challenges them to make the most of their God-given talents while at the same time helping them to develop as young people. 'Art is a unique way of understanding and responding to the world'. In year 9 students develop their creativity and imagination. Topics include 'Image and Text' and 'Card construction Cameras'.", "http://www.st-nicholas.cheshire.sch.uk", "st-nicholas-high-logo.jpg", "st-nicholas-high");
