INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (2, 1, "Childwall Abbey School", "childwall-abbey-school", "Childwall Abbey School is a secondary school which provides quality education for pupils who have complex learning difficulties including Autistic Spectrum Disorder. We believe that all our pupils can achieve, if they are provided with the curriculum, learning environment and support, which meets their needs. At Childwall Abbey, we develop independence and self-esteem in all our pupils through a broad and balanced curriculum to develop skills for adult life, achieve nationally accredited certification and become active contributors to the wider community.", "http://www.childwallabbeyschool.net/", "childwall-abbey-logo.jpg", "childwall-abbey-school");
