INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Blackmoor Park Junior School", "blackmoor-park-junior", "Blackmoor Park Junior School encourages a lifelong enjoyment of learning which leads to personal growth and fulfilment. Our Mission Statement, 'By Living Together, Learning Together and Playing Together We Will All Achieve' reflects this and forms the basis of all our work in the school. Our Year 5 children were encouraged to produce artwork which explored their individually chosen themes, compositions and techniques. They employed a range of different materials including pastel, paint, collage and mixed-media.", "http://www.blackmoorpark.com", "blackmoor-park-junior-logo.jpg", "blackmoor-park-junior");
