INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (2, 1, "The Academy of Saint Nicholas", "the-academy-of-saint-nicholas", "Together we Aspire, Believe and Achieve guided by Christian Values of respect, ambition, resilience and compassion. The Art department is an area of excellence and has a strong team spirit.  The aim of the department is to develop the confidence of students and allow them to explore different issues in a creative and imaginative way.  We believe that Art and Design is a unique subject combining visual, emotional, expressive, conceptual and tactile experiences.", "http://www.theacademyofstnicholas.org.uk/", "academy-of-st-nicholas-logo.jpg", "the-academy-of-saint-nicholas");
