INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Belle Vale Primary School", "belle-vale-primary-school", "Belle Vale Primary School is a friendly, ambitious and inclusive school where children come first. We are striving to achieve the highest possible standards in all aspects of learning in a supportive environment where all are inspired to be their best. Year 5 have been exploring a range of media inspired by the work of Henri Rousseau", "http://www.bellevaleprimary.co.uk/", "belle-vale-primary-logo.jpg", "belle-vale-primary");
