INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Bidston Avenue Primary School", "bidston-avenue-primary-school", "Art is an integral part of our curriculum enabling children to display their creativity and individual expression.  The teaching of Art within our school provides an understanding and experience of many different art forms including; drawing, painting, 3D design and sculpture as well as digital media.  These areas are developed continuously throughout the school.  The children are inspired by the work of famous artists as well being given the opportunities to work with external artists on projects.  The artwork the children have produced has been created using different mediums including paint and digital media.", "http://www.bidstonavenue.wirral.sch.uk/", "bidston-avenue-primary-logo.jpg", "bidston-avenue-primary");
