INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Dovedale Primary School", "dovedale-primary-school", "Taking inspiration from artist Ted Harrison's landscape paintings, we had to mix our own acrylic colour pallet to produce a landscape painting.", "http://www.dovedaleprimary.co.uk", "dovedale-primary-logo.jpg", "dovedale-primary");
