INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Lander Road Primary School", "lander-road-primary-school", "Our artwork is inspired by nature and our journey as learners. The title of our art is our school motto, 'From tiny acorns mighty oak trees grow'. We chose colours to represent the different emotions we will experience during our lives; we applied these using watercolour washes. The branches represent all of the different paths our lives may take, we used masking fluid to draw our trees. Our art symbolises opportunity and growth. Just like tree’s we will grow and flourish.", "http://www.lander-road.co.uk/", "lander-road-primary-logo.jpg", "lander-road-primary");
