INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Our Lady of Good Help Catholic Primary", "our-lady-of-good-help-catholic-primary", "Our school is a small and friendly one-form entry Catholic primary school situated in the heart of Wavertree. We are proud of our children who are eager to learn and our staff, who put care and thought into everything that they do. In Year 5 we have been learning about the Vikings and have read How To Train Your Dragon - this inspired our artwork!", "http://www.olgh.co.uk", "our-lady-of-good-help-logo.jpg", "our-lady-of-good-help");
