INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Our Lady of Lourdes Catholic Primary School", "our-lady-of-lourdes-catholic-primary", "At Our Lady of Lourdes we believe that Art and Design is a means of communication that can help children to explore, understand, share and represent their physical and human environment. They are encourages to experiment with a range of materials and equipment and techniques in areas such as printing, painting and mark marking.  For this competition our Year 5 children used the mono-printing technique, a wonderfully experimental and creative form of printing. After taking a trip to Liverpool City their artwork was inspired by famous landmarks and images that they had seen.", "www.ololprimary.co.uk", "our-lady-of-lourdes-logo.jpg", "our-lady-of-lourdes-catholic-primary");
