INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Our Lady & St. Philomena's Catholic Primary School", "our-lady-and-st-philomenas-catholic-primary", "Our School is Christ centred where Gospel values permeate our daily lives. We extend our hand of friendship to the wider community in the spirit of love, peace and justice. We create opportunities to celebrate the uniqueness of every child and nurture them through a stimulating and enriching curriculum.", "http://www.olasp.co.uk/", "our-lady-and-st-philomenas-logo.jpg", "our-lady-and-st-philomenas-catholic-primary");
