INSERT INTO school (type, region, name, url_path, statement, website, image, image_folder) VALUES (1, 1, "Bishop Martin CofE Primary", "bishop-martin-ce-primary-school", "Bishop Martin is a thriving church school in the heart of Woolton village where the children really enjoy art. Year 5 have been exploring reduction press printing, oil pastel landscapes, mixed media space art and have used matchsticks to respond to the work of Alberto Giacometti.", "http://www.bishopmartince.co.uk/", "bishop-martin-ce-primary-logo", "bishop-martin-ce-primary");
