DROP TABLE IF EXISTS judging;

CREATE TABLE judging (
 entry_id     smallint,
 school       varchar(50),
 title        varchar(50),
 artist       varchar(50)
);


UPDATE judging j
  JOIN entry e ON j.`artist` = e.`artist` AND j.`title` = e.`title`
   SET j.`entry_id` = e.`entry_id`;

UPDATE entry e
  JOIN judging j ON j.`artist` = e.`artist` AND j.`title` = e.`title`
   SET e.`rank` = 1;
